$(document).ready(function(){$("#toTop").click(function(){$("html, body").animate({scrollTop:0},"slow")})}),$(document).ready(function(){$(".carousel").carousel()});const buttons=document.querySelectorAll(".language button");buttons.forEach(t=>{t.addEventListener("click",()=>{buttons.forEach(t=>{t.classList.contains("bold-button")&&t.classList.remove("bold-button")}),t.classList.add("bold-button")})});